INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (1, 'libxUtils.so', 'xUtils', 'xUtils', '一个快速开发框架。主要由4个模块组成：DbUtils模块、ViewUtils模块、HttpUtils模块和itmapUtils模块。地址：[https://github.com/wyouflf/xUtils3]', 'https://github.com/wyouflf/xUtils3', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (2, 'libVolley.so', 'Volley', 'Volley', 'google 推荐网络请求库，适用于多个小数量请求；缺点是没有自带上传和下载文件的功能，不支持对bitmap处理。地址：[https://github.com/mcxiaoke/android-volley]', 'https://github.com/mcxiaoke/android-volley', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (3, 'libNetroid.so', 'Netroid', 'Netroid', 'Volley的扩展库，供执行网络请求、缓存返回结果、批量图片加载、大文件断点下载的常见Http交互功能。地址：[https://github.com/vince-styling/Netroid]', 'https://github.com/vince-styling/Netroid', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (4, 'libFastJson.so', 'FastJson', 'FastJson', '在Android开发中,我们Android客户端如果要和服务器端交互,一般都会采用json数据格式进行交互,FastJson是一个Json处理工具包，包括“序列化”和“反序列化”两部分,Fastjson是一个Java语言编写的高性能功能完善的JSON库。一个JSON库涉及的最基本功能就是序列化和反序列化。Fastjson支持java bean的直接序列化。你可以使用com.alibaba.fastjson.JSON这个类进行序列化和反序列化。fastjson采用独创的算法，将parse的速度提升到极致，超过所有json库。地址：[https://github.com/alibaba/fastjson]', 'https://github.com/alibaba/fastjson', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (5, 'libGson.so', 'Gson', 'Gson', 'Google 提供的用来在 Java 对象和 JSON 数据之间进行映射的 Java 类库。地址：[https://github.com/google/gson]', 'https://github.com/google/gson', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (6, 'libAndroid-async-http.so', 'Android-async-http', 'Android-async-http', 'android-async-http是专门针对Android在Apache的HttpClient基础上构建的异步http连接。地址：[https://github.com/loopj/android-async-http]', 'https://github.com/loopj/android-async-http', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (7, 'libAfinal.so', 'Afinal', 'Afinal', '一个开源的android的orm和ioc应用开发框架。主要有四大模块：数据库模块、注解模块、网络模块、图片缓存模块。地址：[https://github.com/yangfuhai/afinal]', 'https://github.com/yangfuhai/afinal', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (8, 'libThinkAndroid.so', 'ThinkAndroid', 'ThinkAndroid', 'ThinkAndroid是包含Android mvc和简易sqlite orm以及ioc模块，它封装了Android httpclitent中的http模块, 具有快速构建文件缓存功能，无需考虑什么格式的文件，都可以非常轻松的实现缓存，它实现了图片缓存，在android中 加载的图片的时候oom的问题和快速滑动的时候图片加载位置错位等问题都可以轻易的解决掉。他还包括了一个手机开发中 经常应用的实用工具类，如日志管理，配置文件管理，android下载器模块，网络切换检测等等工具。它主要包含的模块有：MVC模块、ioc模块、数据库模块、http模块、缓存模块、图片缓存模块、配置器模块、日志打印模块、下载器模块、网络状态检测模块。地址：[https://github.com/white-cat/ThinkAndroid]', 'https://github.com/white-cat/ThinkAndroid', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (9, 'libLoonAndroid.so', 'LoonAndroid', 'LoonAndroid', '它主要包括以下几部分：自动注入框架、图片加载框架、网络请求模块、eventbus、验证框架、json解析、数据库、多线程断点下载、自动更新模块、一系列工具类。地址：[https://github.com/gdpancheng/LoonAndroid]', 'https://github.com/gdpancheng/LoonAndroid', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (10, 'libKJFrameForAndroid.so', 'KJFrameForAndroid', 'KJFrameForAndroid', 'KJFrameForAndroid 又叫KJLibrary，是一个android的orm 和 ioc 框架。同时封装了android中的Bitmap与Http操作的框架，使其更加简单易用；总共分为五大模 块：UILibrary，UtilsLibrary，HttpLibrary，BitmapLibrary，DBLibrary。地址：[https://github.com/kymjs/KJFrameForAndroid]', 'https://github.com/kymjs/KJFrameForAndroid', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (11, 'libAndroidAnnotations.so', 'AndroidAnnotations', 'AndroidAnnotations', '这是国外的一个框架主要专注于注解的开发，简化Android代码编写，它除了有依赖注入的特性以外，还集成了Ormlite，spring-android中的REST模板。地址：[https://github.com/excilys/androidannotations]', 'https://github.com/excilys/androidannotations', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (12, 'libormlite-android.so', 'ormlite-android', 'ormlite-android', '主要依赖注解和反射的方式；使用简单，易上手。地址：[https://github.com/j256/ormlite-android]', 'https://github.com/j256/ormlite-android', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (13, 'libGreenDao.so', 'GreenDao', 'GreenDao', '由greenrobot提供开发，是一个将对象映射到 SQLite 数据库中的轻量且快速的 ORM 解决方案。地址：[https://github.com/greenrobot/greenDAO]', 'https://github.com/greenrobot/greenDAO', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (14, 'libButterKnife.so', 'ButterKnife', 'ButterKnife', '这个开源库可以让我们从大量的findViewById()和setonclicktListener()解放出来，其对性能的影响微乎其微。地址：[https://github.com/JakeWharton/butterknife]', 'https://github.com/JakeWharton/butterknife', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (15, 'libokHttp.so', 'okHttp', 'okHttp', '由Square公司出品，目前相对比较成熟的网络请求库，获得过google研发人员的称赞；支持断点续传，文件下载等功能。地址:[https://github.com/square/okhttp]', 'https://github.com/square/okhttp', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (16, 'libRetrofit.so', 'Retrofit', 'Retrofit', '由Square公司出品，使用起来也是非常简单的，主要也是通过注解的方式简化了接口的定义；可配合okHttp来使用。地址：[https://github.com/square/retrofit]', 'https://github.com/square/retrofit', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (17, 'libDiskLruCache.so', 'DiskLruCache', 'DiskLruCache', '硬盘缓存的最佳方案，获得官方认证。地址：[https://github.com/JakeWharton/DiskLruCache]', 'https://github.com/JakeWharton/DiskLruCache', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (18, 'libImage-Loader.so', 'Image-Loader', 'Image-Loader', '配置性较强；支持加载网络，文件系统，项目文件夹assets中以及drawable中的图片。地址：[https://github.com/nostra13/Android-Universal-Image-Loader]', 'https://github.com/nostra13/Android-Universal-Image-Loader', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (19, 'libPicasso.so', 'Picasso', 'Picasso', '实现简单，一行代码就能完全实现图片的异步加载；解决adapter的错乱等问题。地址：[https://github.com/square/picasso]', 'https://github.com/square/picasso', 0);
