INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (21, 'libRxJava.so', 'RxJava', 'RxJava', '是一个响应式编程框架，采用观察者设计模式。地址：[https://github.com/ReactiveX/RxJava]', 'https://github.com/ReactiveX/RxJava', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (22, 'libEventBus.so', 'EventBus', 'EventBus', '由greenrobot提供开发；针对Android优化的发布/订阅事件总线,代码量小，可灵活在类之间传递数据。地址：[https://github.com/greenrobot/EventBus]', 'https://github.com/greenrobot/EventBus', 0);
