INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (33, 'libjpush.so', 'jpush', 'jpush', '极光推送的sdk，可用来推送系统消息和通知。地址：[http://docs.jpush.io/]', 'http://docs.jpush.io/', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (34, 'libumeng.so', 'umeng', 'umeng', '友盟统计的sdk。友盟会在app出现崩溃的时候自动上传崩溃日志到后台，通过友盟的错误分析功能可以看到崩溃发生的地方以及崩溃日志。通过分析这些日志可以了解到哪部分代码有问题。如果代码被混淆过，还可以上传mapping文件到友盟后台，这样看到的崩溃日志就是没有混淆过的。地址：[https://www.umeng.com/]', 'https://www.umeng.com/', 0);
INSERT INTO `StaticAnalyzer_so_and_sdk` VALUES (35, 'libZXing.so', 'ZXing', 'ZXing', '由Google开发的，实现二维码的生成与解析。地址：[https://github.com/zxing]', 'https://github.com/zxing', 0);
